import { Injectable } from '@angular/core';
import { CanActivate, Router } from '@angular/router';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';
import { InitialConfigService } from '../services/initial-config.service';

@Injectable({
  providedIn: 'root',
})
export class initialConfigGuard implements CanActivate {
  constructor(
    private _initialConfigService: InitialConfigService,
    private _router: Router
  ) { }

  canActivate(): Observable<boolean> | Promise<boolean> | boolean {
    return this._initialConfigService.InitialCheck().pipe(
      map(response => {
        if (response.setupRequired) {
          // Si se requiere configuración inicial, permitir el acceso
          return true;
        } else if (response.verificationRequired) {
          // Si el MasterAdministrator no está verificado, redirigir a la página de reenvío de correo
          this._router.navigate(['/initial-config/resend-password']);
          return false;
        } else {
          // En cualquier otro caso, redirigir al login
          this._router.navigate(['/auth/login']);
          return false;
        }
      })
    );
  }
}
