import { Component, OnInit } from '@angular/core';
import { AuthService } from 'src/app/core/auth/auth.service';
import { Router } from "@angular/router";
import { UIBootstrapService } from 'src/app/core/services/uibootstrap.service';


@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent implements OnInit {

  isCollapsed = true;
  public user: any = { data: {} };

  constructor(
    private _authService: AuthService,
    private _router: Router,
    private _uiInitService: UIBootstrapService) { }

  ngOnInit(): void {
    this.fetchUserData();

  }

  fetchUserData() {
    const token = localStorage.getItem('token');
    this._authService.get_admin(token).subscribe(
      response => {
        this.user = response;
        this._uiInitService.activateFeatherIcons();
      },
      error => {
        console.error(error);
      }
    );
  }

  logout(): void {
    this._authService.logout();
    this._router.navigate(['login']);
  }

  accountSetting(): void {
    this._router.navigate(['profile']);
  }
}
