import { Component } from '@angular/core';
import { GLOBAL } from 'src/app/core/config/GLOBAL';
import { FormBuilder, FormGroup, Validators, FormControl } from "@angular/forms";
import { Router } from '@angular/router';
import { AuthService } from 'src/app/core/auth/auth.service';
import { ToastService } from 'src/app/shared/services/toast.service';
import { ProfileData } from 'src/app/core/model/profile-data';


@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.css']
})
export class ProfileComponent {
  updateForm!: FormGroup;
  public user: any = { data: {} };

  constructor(
    private _formBuilder: FormBuilder,
    private _authService: AuthService,
    private _router: Router,
    private _toastService: ToastService
  ) {
    this.updateForm = this._formBuilder.group({
      userName:['', [Validators.required]],
      firstName:['', [Validators.required]],
      lastName:['', [Validators.required]],
      email: ['', [Validators.required, Validators.email]],
      location:['', [Validators.required]],
      phoneNumber:['', [Validators.required]],
      role:['', [Validators.required]],
      identification:['', [Validators.required]],
      additionalInfo:['', [Validators.required]],
      profileImage:['', [Validators.required]],
    });
  }

  get f() {
    return this.updateForm.controls;
  }


  ngOnInit(): void {
    this.fetchUserData();
  }


  fetchUserData() {
    this._authService.get_admin().subscribe(
      response => {
        this.user = response;
        console.log("ðŸš€ ~ file: profile.component.ts:48 ~ ProfileComponent ~ fetchUserData ~ this.user:", this.user)
        if (response.data === undefined) {
          this._router.navigate(['']);
        } else {
          const { userName, firstName, lastName, email, location, phoneNumber, role, identification, additionalInfo, profileImage, createdAt, updatedAt } = response.data;
          this.updateForm.patchValue({
            userName: userName,
            firstName: firstName,
            lastName: lastName,
            email: email,
            location: location,
            phoneNumber: phoneNumber,
            role: role,
            identification: identification,
            additionalInfo: additionalInfo,
            profileImage: profileImage,
            createdAt: createdAt,
            updatedAt: updatedAt,
          });

        }

      },
      error => {
        console.error(error);
      }
    );
  }


  update() {}


}
