import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from 'src/app/core/auth/auth.service';
import { ToastService } from 'src/app/shared/services/toast.service';
import { FormGroup, FormBuilder, Validators } from '@angular/forms';
import { catchError, finalize } from 'rxjs/operators';
import { throwError } from 'rxjs';


interface ApiResponse {
  data?: any;
  token?: string;
}

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css'],
  changeDetection: ChangeDetectionStrategy.OnPush // Mejora en la detección de cambios

})
export class LoginComponent implements OnInit {
  public user: any = {};
  public token: any = '';
  public loginForm: FormGroup;

  constructor(
    private formBuilder: FormBuilder,
    private _authService: AuthService,
    private _router: Router,
    private _toastService: ToastService
  ) {
    this.token = this._authService.getToken();
    this.loginForm = this.formBuilder.group({
      email: ['', [Validators.required, Validators.email]],
      password: ['', Validators.required],
    });
  }

  ngOnInit(): void {
    if (this.token) {
      this._router.navigate(['']);
    }
  }

  login() {
    if (this.loginForm.valid) {
      const { email, password } = this.loginForm.value;
      const data = { email, password };
      this._authService.login_admin(data).subscribe(
        (response: ApiResponse) => {
          if (!response.data) {
            this._toastService.showToast('error', 'There was an error during login');
          } else {
            this.handleSuccessfulLogin(response);
          }
        },
        (error) => {
          this._toastService.showToast('error', 'There was an error during login');
          console.error(error);
        }
      );
    } else {
      this._toastService.showToast('error', 'Invalid form data');
    }
  }

  private handleSuccessfulLogin(response: ApiResponse) {
    this.user = response.data;
    localStorage.setItem('token', response.token || '');
    this._router.navigate(['/']);
  }

}

