import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { FormBuilder, FormGroup } from '@angular/forms';
import { NotificationPreferencesService } from 'ruta/a/tu/servicio'; // Asegúrate de importar el servicio correcto

@Component({
  selector: 'app-preferences',
  templateUrl: './preferences.component.html',
  styleUrls: ['./preferences.component.css']
})
export class PreferencesComponent {
  preferencesForm: FormGroup;

  constructor(
    private router: Router,
    private formBuilder: FormBuilder,
    private notificationPreferencesService: NotificationPreferencesService // Tu servicio para manejar las preferencias
  ) {
    this.createForm();
  }

  createForm() {
    this.preferencesForm = this.formBuilder.group({
      accountChanges: false,
      groupChanges: false,
      productUpdates: false,
      newProducts: false,
      marketingOffers: false,
      securityAlerts: false
    });
  }

  onSubmit() {
    if (this.preferencesForm.valid) {
      this.notificationPreferencesService.savePreferences(this.preferencesForm.value).subscribe({
        next: () => {
          this.goToNextStep();
        },
        error: (error) => {
          // Manejar el error aquí
        }
      });
    }
  }

  goToNextStep() {
    this.router.navigate(['/initial-config/step4']);
  }

  goToPreviousStep() {
    this.router.navigate(['/initial-config/step2']);
  }
}
