import { Injectable } from '@angular/core';
import { CanActivate, Router } from '@angular/router';
import { InitialConfigService } from '../services/initial-config.service';

@Injectable({
  providedIn: 'root',
})
export class RootGuard implements CanActivate {
  constructor(
    private initialConfigService: InitialConfigService,
    private router: Router
  ) { }

  canActivate(): boolean {
    const setupRequired = this.initialConfigService.needsInitialConfiguration();
    if (setupRequired === null) {
      // Si no se ha verificado aún, realiza la verificación y guarda el resultado
      this.initialConfigService.InitialCheck().subscribe(response => {
        if (response.setupRequired) {
          this.router.navigate(['/initial-config']);
        } else {
          this.router.navigate(['/auth']);
        }
      });
      return false; // Detiene la navegación hasta que la verificación se complete
    } else if (setupRequired) {
      this.router.navigate(['/initial-config']);
      return false;
    }
    return true;
  }
}
