import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Router } from '@angular/router';
import { Observable, BehaviorSubject } from 'rxjs';
import { map } from 'rxjs/operators';
import { JwtHelperService } from '@auth0/angular-jwt';

import { GLOBAL } from '../config/GLOBAL';
import { User, UserWithToken } from '../model/user.interface';
import { LoginCredentials } from '../model';

@Injectable({
  providedIn: 'root',
})
export class AuthService {
  private url: string;
  private jwtHelper: JwtHelperService;
  private user = new BehaviorSubject<UserWithToken | null>(null);
  public user$ = this.user.asObservable();
  public isLoggedIn$: Observable<boolean> = this.user$.pipe(map(user => !!user));

  constructor(private _http: HttpClient, private _router: Router) {
    this.url = GLOBAL.url;
    this.jwtHelper = new JwtHelperService();
  }

  public isAuthenticated(allowRoles: string[]): boolean {
    const token = localStorage.getItem('token');
    if (!token) return false;

    try {
      const decodedToken = this.jwtHelper.decodeToken(token);
      const isValid = allowRoles.includes(decodedToken['role']);
      if (isValid) {
        this.user.next({ ...decodedToken, token });
      } else {
        this.user.next(null);
      }
      return isValid;
    } catch (error) {
      console.error('Error decoding token:', error);
      localStorage.removeItem('token');
      this.user.next(null);
      return false;
    }
  }

  login(credentials: LoginCredentials): Observable<any> {
    return this._http.post(this.url + 'login', credentials, {
      headers: new HttpHeaders().set('Content-Type', 'application/json'),
    }).pipe(
      map(response => {
        // Asumiendo que el token es parte de la respuesta
        const token = response.token;
        localStorage.setItem('token', token);
        const userInfo = this.jwtHelper.decodeToken(token);
        this.user.next({ ...userInfo, token });
        return response;
      })
    );
  }


  logout(): void {
    localStorage.removeItem('token');
    this.user.next(null);
    this._router.navigate(['/login']);
  }

  check_admin_exists(): Observable<any> {
    return this._http.get<any>(this.url + 'check_admin_exists', { 
      headers: new HttpHeaders().set('Content-Type', 'application/json') 
    });
  }

  createAdmin(data: any): Observable<any> {
    return this._http.post(this.url + 'create_admin', data, { 
      headers: new HttpHeaders().set('Content-Type', 'application/json') 
    });
  }

  get_admin(token: any): Observable<any> {
    return this._http.get(this.url + 'get_admin', { 
      headers: new HttpHeaders({ 'Content-Type': 'application/json', 'Authorization': token }) 
    });
  }
}

