//initial-config.service.ts
import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Router } from '@angular/router';
import { GLOBAL } from '../config/GLOBAL';
import { Observable, catchError, map } from 'rxjs';


@Injectable({
  providedIn: 'root'
})
export class InitialConfigService {

  private url: string = GLOBAL.url;
  constructor(private _http: HttpClient, private _router: Router) { }

  // InitialCheck(): Observable<any> {
  //   return this._http.get<any>(`${this.url}InitialCheck`);
  // }

  InitialCheck(): Observable<boolean> {
    return this.http.get<{ status: string, message: string, data: { setupRequired: boolean } }>(`${this.url}/InitialCheck`)
      .pipe(
        map(response => response.data.setupRequired),
        catchError(error => {
          // Manejo de errores
          console.error('Error en InitialCheck:', error);
          return of(false); // O maneja el error de la forma que consideres apropiada
        })
      );
  }

  createMasterAdmin(data: any): Observable<any> {
    return this._http.post(`${this.url}createMasterAdmin`, data);
  }

}
