import { Component, OnInit, OnDestroy } from '@angular/core';
import { UIBootstrapService } from 'src/app/core/services/uibootstrap.service';

@Component({
  selector: 'app-billing-details',
  templateUrl: './billing-details.component.html',
  styleUrls: ['./billing-details.component.css']
})
export class BillingDetailsComponent implements OnInit, OnDestroy {

  constructor(private uiBootstrapService: UIBootstrapService) { }

  ngOnInit(): void {
    this.uiBootstrapService.activateFeatherIcons();
    this.uiBootstrapService.enableTooltipsGlobally();
    this.uiBootstrapService.enablePopoversGlobally();
    this.uiBootstrapService.activateScrollspyForStickyNav();
    this.uiBootstrapService.toggleSideNavigation();
    this.uiBootstrapService.closeSideNavigationOnWidthChange();
    this.uiBootstrapService.addActiveStateToSidebarNavLinks();
  }

  ngOnDestroy(): void {
    // Aquí puedes deshacer cualquier inicialización si es necesario
  }
}
