import { NgModule } from '@angular/core';
import { CommonModule } from '@angular/common';

// Importa componentes, directivas, pipes aquí
import { ToastMessagesComponent } from '../toast-messages.component';

@NgModule({
  declarations: [
    // Declara componentes, directivas, pipes aquí
    ToastMessagesComponent
  ],
  imports: [
    CommonModule // Otros módulos comunes
  ],
  exports: [
    // Exporta componentes, directivas, pipes que quieras compartir
    ToastMessagesComponent
  ]
})
export class SharedModule { }
