import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Router } from '@angular/router';
import { BehaviorSubject, Observable, tap } from 'rxjs';
import { JwtHelperService } from '@auth0/angular-jwt';

import { GLOBAL } from '../config/GLOBAL';
import { UserWithToken } from '../model/user.interface';
import { LoginCredentials } from '../model';

@Injectable({
  providedIn: 'root',
})
export class AuthService {
  private user = new BehaviorSubject<UserWithToken | null>(null);
  public user$ = this.user.asObservable();

  private url: string;

  constructor(
    private _http: HttpClient,
    private _router: Router,
    private jwtHelper: JwtHelperService
  ) {
    this.url = GLOBAL.url;
  }

  public isAuthenticated(allowRoles: string[]): boolean {
    const token = localStorage.getItem('token');
    if (!token) return false;

    try {
        const decodedToken = this.jwtHelper.decodeToken(token);
        return allowRoles.includes(decodedToken['role']);
    } catch (error) {
        console.error('Error decoding token:', error);
        localStorage.removeItem('token');
        return false;
    }
  }

  login(credentials: LoginCredentials): Observable<any> {
    return this._http.post<string>(this.url + 'login', credentials, {
      headers: new HttpHeaders().set('Content-Type', 'application/json'),
    }).pipe(
      tap((response: any) => {
        const token = response.token; // Asumiendo que el token está en response.token
        if (typeof token === 'string') {
          localStorage.setItem('token', token);
          this.user.next(this.decodeToken(token));
        } else {
          console.error('Token recibido no es una cadena de texto');
        }
      })
    );
  }


  logout(): void {
    localStorage.removeItem('token');
    this.user.next(null);
    this._router.navigate(['/login']);
  }

  private decodeToken(token: string): UserWithToken {
    const decodedToken = this.jwtHelper.decodeToken(token);
    return { ...decodedToken, token };
  }

  check_admin_exists(): Observable<any> {
    return this._http.get<any>(this.url + 'check_admin_exists', { 
      headers: new HttpHeaders().set('Content-Type', 'application/json') 
    });
  }

  createAdmin(data: any): Observable<any> {
    return this._http.post(this.url + 'create_admin', data, { 
      headers: new HttpHeaders().set('Content-Type', 'application/json') 
    });
  }

  get_admin(token: any): Observable<any> {
    return this._http.get(this.url + 'get_admin', { 
      headers: new HttpHeaders({ 'Content-Type': 'application/json', 'Authorization': token }) 
    });
  }
}

