//initial-config.service.ts
import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Router } from '@angular/router';
import { JwtHelperService } from '@auth0/angular-jwt';
import { LoginCredentials } from 'src/app/core/models';
import { GLOBAL } from '../config/GLOBAL';
import { Observable, throwError, Subject, EMPTY } from 'rxjs';
import { tap, catchError } from 'rxjs/operators';

@Injectable({
  providedIn: 'root'
})
export class InitialConfigService {

  private url: string = GLOBAL.url;
  constructor(private _http: HttpClient, private _router: Router) { }

  // Suponiendo que se guarda el resultado de la verificación en localStorage
  needsInitialConfiguration(): boolean | null {
    return localStorage.getItem('setupRequired') === 'true' ? true :
      localStorage.getItem('setupRequired') === 'false' ? false : null;
  }

  InitialCheck(): Observable<any> {
    return this._http.get<any>(`${this.url}InitialCheck`);
  }

  createMasterAdmin(data: any): Observable<any> {
    return this._http.post(`${this.url}createMasterAdmin`, data);
  }

}
