import { Injectable } from '@angular/core';
import { CanMatch, Router, UrlTree, Route } from '@angular/router';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';
import { AuthService } from '../auth.service';

@Injectable({
  providedIn: 'root',
})
export class IsLoggedInGuard implements CanMatch {
  constructor(
    private authService: AuthService, 
    private router: Router
    ) {}

  canMatch(route: Route): Observable<boolean | UrlTree> {
    return this.authService.isLoggedIn$.pipe(
      map((isLoggedIn) => isLoggedIn || this.router.createUrlTree(['auth/login']))
    );
  }
}

