// activation.component.ts
import { ActivatedRoute } from '@angular/router';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { 
@Component({
  selector: 'app-activation',
  templateUrl: './activation.component.html',
  styleUrls: ['./activation.component.css']
})
export class ActivationComponent implements OnInit {
  activationForm: FormGroup;
  token: string;

  constructor(
    private route: ActivatedRoute,
    private formBuilder: FormBuilder,
    private authService: AuthService // Suponiendo que tienes un servicio para autenticación
  ) { }

  ngOnInit(): void {
    this.token = this.route.snapshot.paramMap.get('token');
    this.activationForm = this.formBuilder.group({
      password: ['', [Validators.required, Validators.minLength(8)]],
      confirmPassword: ['', [Validators.required]]
    });
    // Agrega validaciones adicionales según sea necesario
  }

  onActivate() {
    if (this.activationForm.valid) {
      this.authService.activateAccount(this.token, this.activationForm.value.password).subscribe(
        response => {
          // Maneja la respuesta
        },
        error => {
          // Maneja los errores
        }
      );
    }
  }
}
