import { Injectable } from '@angular/core';
import { CanActivate, Router } from '@angular/router';
import { InitialConfigService } from '../services/initial-config.service';

@Injectable({
  providedIn: 'root',
})
export class RootGuard implements CanActivate {
  constructor(
    private initialConfigService: InitialConfigService,
    private router: Router
  ) { }

  canActivate(): Observable<boolean> {
    return this.initialConfigService.InitialCheck().pipe(
      map(response => {
        if (response.data.setupRequired) {
          // Redirige a la configuración inicial solo si estamos en una ruta diferente
          if (this.router.url !== '/initial-config') {
            this.router.navigate(['/initial-config']);
          }
          return false;
        }
        // Si no se necesita configuración inicial, permite la navegación
        return true;
      }),
      catchError(() => {
        // En caso de error, puedes decidir qué hacer, como redirigir al login o mostrar un mensaje
        this.router.navigate(['/auth/login']);
        return of(false);
      })
    );
  }
}
