// isLoggedIn.guard.ts
import { CanActivateFn } from '@angular/router';
import { AuthService } from 'ruta/a/tu/auth.service';

export function isLoggedInGuardFactory(authService: AuthService): CanActivateFn {
  return (route, state) => {
    const allowRoles = ['admin', 'user']; // Ajusta los roles según sea necesario
    if (authService.isAuthenticated(allowRoles)) {
      return true;
    } else {
      authService.redirectTo('/login');
      return false;
    }
  };
}
