import { Injectable } from '@angular/core';
import { CanActivate, Router, UrlTree, ActivatedRouteSnapshot, RouterStateSnapshot } from '@angular/router';
import { AuthService } from '../services/auth.service';
import { InitialConfigService } from '../services/initial-config.service';

@Injectable({
  providedIn: 'root',
})
export class isLoggedInGuard implements CanActivate {
  constructor(
    private authService: AuthService,
    private initialConfigService: InitialConfigService,
    private router: Router
  ) { }

  canActivate(
    next: ActivatedRouteSnapshot,
    state: RouterStateSnapshot
  ): boolean | UrlTree {
    const setupRequired = this.initialConfigService.getSetupRequiredSync();

    if (setupRequired === null) {
      // Redirigir a una página de carga o manejar según sea necesario
      return this.router.createUrlTree(['/loading']); // Asegúrate de que esta ruta exista
    } else if (setupRequired) {
      // Si 'setupRequired' es true, redirige a la configuración inicial
      return this.router.createUrlTree(['/auth/initial-setup']);
    } else {
      // Si 'setupRequired' es false, verifica la autenticación
      if (this.authService.isAuthenticated(['MasterAdministrator'])) {
        return true;
      } else {
        // Si el usuario no está autenticado, redirige al login
        return this.router.createUrlTree(['/auth/login']);
      }
    }
  }
}
