import { Component } from '@angular/core';

@Component({
  selector: 'app-initial-config',
  template: `
    <div>
      <app-step1 *ngIf="currentStep === 1"></app-step1>
      <app-step2 *ngIf="currentStep === 2"></app-step2>
      <app-step3 *ngIf="currentStep === 3"></app-step3>
      <!-- Botones de navegación -->
    </div>
  `
})
export class InitialConfigComponent {
  currentStep = 1;

  // Funciones para manejar la navegación entre pasos
}
