import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { InitialConfigService } from 'src/app/core/services/initial-config.service';
import { ValidationService } from 'src/app/core/services/validation.service'; // Importa el servicio de validaci√≥n


@Component({
  selector: 'app-activation',
  templateUrl: './activation.component.html',
  styleUrls: ['./activation.component.css']
})

export class ActivationComponent implements OnInit {
  activationForm!: FormGroup; // Usa "!" para indicar que ser√° inicializada en ngOnInit
  token!: string; // Usa "!" para indicar que ser√° inicializada en ngOnInit

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private formBuilder: FormBuilder,
    private _validationService: ValidationService,
    private _initialConfigService: InitialConfigService
  ) { }

  ngOnInit(): void {
    this.activationForm = this.formBuilder.group({
      inputPassword: ['', [Validators.required, Validators.minLength(8)]],
      inputConfirmPassword: ['', [Validators.required]]
    }, {
      validator: this._validationService.mustMatch('inputPassword', 'inputConfirmPassword') // Aplica la validaci√≥n mustMatch
    });

    this.route.params.subscribe(params => {
      this.token = params['token'];
    });
  }

  onActivate() {
    if (this.activationForm.valid) {
      const password = this.activationForm.value.inputPassword;
      console.log("üöÄ ~ ActivationComponent ~ onActivate ~ password:", password)

      // Llama a tu servicio para activar la cuenta
      this._initialConfigService.activateAccount(this.token, password).subscribe(
        response => {
          // Maneja la respuesta exitosa
          // Redirige a una p√°gina de √©xito o a donde sea necesario
          this.router.navigate(['initial/activation-success']);
        },
        error => {
          // Maneja los errores, por ejemplo, muestra un mensaje de error en la p√°gina
          console.error('Error al activar la cuenta:', error);
        }
      );
    }
  }

}
