import { Component } from '@angular/core';
import { GLOBAL } from 'src/app/core/config/GLOBAL';
import {
  FormBuilder,
  FormGroup,
  Validators,
  FormControl,
} from '@angular/forms';
import { Router } from '@angular/router';
import { AuthService } from 'src/app/core/auth/auth.service';
import { ToastService } from 'src/app/shared/services/toast.service';
import { ProfileData } from 'src/app/core/models/profile-data.interface';
import { GeoInfoService } from 'src/app/shared/services/geo-info.service';
import { Country } from 'src/app/core/models/country.model';
import { State } from 'src/app/core/models/state.model';

@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.css'],
})
export class ProfileComponent {
  updateForm!: FormGroup;
  submitted = false;
  public user: ProfileData | null = null;
  countries: Country[] = [];
  states: State[] = [];
  filteredStates: State[] = [];


  constructor(
    private _formBuilder: FormBuilder,
    private _authService: AuthService,
    private _router: Router,
    private _toastService: ToastService,
    private _geoInfoService: GeoInfoService
  ) {
    this.updateForm = this._formBuilder.group({
      inputUserName: [
        '',
        [
          Validators.required,
          Validators.minLength(12),
          Validators.maxLength(25),
          Validators.pattern(/^\S*$/),
          Validators.pattern(/^[a-zA-Z0-9]*$/),
        ],
      ],
      inputFirstName: [
        '',
        [
          Validators.required,
          Validators.minLength(3),
          Validators.maxLength(25),
          Validators.pattern('^[a-zA-Z0-9\\sñÑ]+$'),
        ],
      ],
      inputLastName: [
        '',
        [
          Validators.required,
          Validators.minLength(3),
          Validators.maxLength(25),
          Validators.pattern('^[a-zA-Z0-9\\sñÑ]+$'),
        ],
      ],
      inputOrganizationName: [
        '',
        [
          Validators.required,
          Validators.minLength(3),
          Validators.maxLength(30),
          Validators.pattern('^[a-zA-Z0-9\\sñÑ]+$'),
        ],
      ],
      inputEmailAddress: ['', [Validators.required, Validators.email]],
      inputAddress: this._formBuilder.group({
        street1: ['', [Validators.required]],
        street2: [''],
        city: [''],
        state: ['', [Validators.required]],
        zip: [''],
        country: ['', [Validators.required]],
      }),
      inputPhoneNumber: [
        '',
        [Validators.required, Validators.pattern('[0-9]+')],
      ],
      inputBirthday: ['', [Validators.required, this.validateDate.bind(this)]],
      inputRole: [{ value: '', disabled: true }, [Validators.required]],
      inputIdentification: [
        { value: '', disabled: true },
        [Validators.required],
      ],
      inputAdditionalInfo: [
        '',
        [
          Validators.required,
          Validators.minLength(10),
          Validators.maxLength(50),
          Validators.pattern('^[a-zA-Z0-9\\sñÑ]+$'),
        ],
      ],
      inputProfileImage: ['', [Validators.required]],
    });
  }

  ngOnInit(): void {
    this.fetchUserData();
    this.loadCountries();
  }

  fetchUserData() {
    this._authService.get_admin().subscribe(
      (response) => {
        if (response.data === undefined) {
          this._router.navigate(['']);
        } else {
          this.user = response.data as ProfileData;
          this.updateFormWithUserData(this.user);
          // Después de actualizar el formulario, filtra los estados
          const userCountryId = this.user.address.country;
          this.onCountryChange(userCountryId);
        }
      },
      (error) => {
        console.error(error);
      }
    );
  }

  private updateFormWithUserData(userData: ProfileData) {
    const birthdayFormatted = this.convertDateFormat(userData.birthday);
    this.updateForm.patchValue({
      inputUserName: userData.userName,
      inputFirstName: userData.firstName,
      inputLastName: userData.lastName,
      inputOrganizationName: userData.organizationName,
      inputEmailAddress: userData.emailAddress,
      inputPhoneNumber: userData.phoneNumber,
      inputBirthday: birthdayFormatted,
      inputRole: userData.role,
      inputIdentification: userData.identification,
      inputAdditionalInfo: userData.additionalInfo,
      inputProfileImage: userData.profileImage,
      inputCreatedAt: userData.createdAt,
      inputUpdatedAt: userData.updatedAt,
      inputAddress: {
        street1: userData.address.street1,
        street2: userData.address.street2,
        city: userData.address.city,
        state: userData.address.state,
        zip: userData.address.zip,
        country: userData.address.country,
      },
    });
  }

  private loadCountries() {
    this._geoInfoService.get_Countries().subscribe(
      data => {
        this.countries = data.sort((a: Country, b: Country) => a.name.localeCompare(b.name)); this.loadStates();
      },
      error => {
        console.error('Error loading countries', error);
      }
    );
  }

  private loadStates() {
    this._geoInfoService.get_States().subscribe(
      data => {
        this.states = data.sort((a: State, b: State) => a.province_name.localeCompare(b.province_name));
      },
      error => {
        console.error('Error loading states', error);
      }
    );
  }

  onCountryChange(selectElement: HTMLSelectElement) {
    // Comprobar si selectElement no es null
    if (selectElement) {
      const countryId = selectElement.value; // Ahora puedes acceder a 'value' de forma segura
      const numericCountryId = Number(countryId); // Convierte a número si es necesario
      this.filteredStates = this.states.filter(state => state.country_id === numericCountryId);

      // Restablecer el valor de estado si es necesario
      const stateControl = this.updateForm.get('inputState');
      if (stateControl) {
        stateControl.setValue(null); // O establecer a un valor predeterminado si es necesario
      }
    }
  }



  private convertDateFormat(dateString: string): string {
    const date = new Date(dateString);
    const year = date.getFullYear();
    const month = String(date.getMonth() + 1).padStart(2, '0');
    const day = String(date.getDate()).padStart(2, '0');
    return `${year}-${month}-${day}`;
  }

  private validateDate(control: FormControl): { [key: string]: any } | null {
    const inputDateStr: string = control.value;
    if (!/^\d{4}-\d{2}-\d{2}$/.test(inputDateStr)) {
      return { invalidDateFormat: true };
    }
    return null;
  }

  sortByProperty(arr: any[], property: string) {
    return arr.sort((a, b) => a[property].localeCompare(b[property]));
  }

  update() {
    this.submitted = true;
  }

  // onCountryChange() {}
}
