import { Component } from '@angular/core';

@Component({
  selector: 'app-wrapper',
  templateUrl: './wrapper.component.html',
  styleUrls: ['./wrapper.component.css']
})
export class WrapperComponent {
  currentStep = 1;

  constructor() { }

  updateStep(step: number): void {
    this.currentStep = step;
  }
}
