import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Router } from '@angular/router';
import { JwtHelperService } from '@auth0/angular-jwt';
import { LoginCredentials } from 'src/app/core/model';
import { GLOBAL } from '../config/GLOBAL';
import { Observable, throwError } from 'rxjs';
import { tap, catchError } from 'rxjs/operators';

@Injectable({
  providedIn: 'root',
})


export class AuthService {
  private url: string = GLOBAL.url;
  private headers: HttpHeaders = new HttpHeaders({
    'Content-Type': 'application/json',
  });

  constructor(private _http: HttpClient, private _router: Router) {}

  private storeToken(token: string): void {
    localStorage.setItem('token', token);
  }

  private removeToken(): void {
    localStorage.removeItem('token');
  }

  getToken(): string | null {
    return localStorage.getItem('token');
  }

  isAuthenticated(allowRoles: string[]): boolean {
    const token = this.getToken();
    if (!token) return false;

    try {
      const helper = new JwtHelperService();
      const decodedToken = helper.decodeToken(token);
      if (!decodedToken || !decodedToken['role']) {
        this.logout();
        return false;
      }
      return allowRoles.includes(decodedToken['role']);
    } catch (error) {
      this.logout();
      return false;
    }
  }

  login_admin(credentials: LoginCredentials): Observable<any> {
    return this._http
      .post<any>(`${this.url}login_admin`, credentials, { headers: this.headers })
      .pipe(
        tap(response => {
          if (response.token) {
            this.storeToken(response.token);
            this._router.navigate(['/']);
          }
        }),
        catchError(error => this.handleError(error))
      );
  }

  logout(): void {
    this.removeToken();
    this._router.navigate(['/auth/login']);
  }

  private handleError(error: any): Observable<never> {
    // Manejo del error
    let errorMessage = error.error.message || 'An error occurred';
    return throwError(() => new Error(errorMessage));
  }
}


