import { Injectable } from '@angular/core';
import {
  HttpEvent,
  HttpInterceptor,
  HttpHandler,
  HttpRequest,
  HttpErrorResponse,
  HTTP_INTERCEPTORS
} from '@angular/common/http';
import { Observable, throwError } from 'rxjs';
import { catchError, first, switchMap } from 'rxjs/operators';
import { AuthService } from '../auth.service';
import { Router } from '@angular/router';

@Injectable()
export class CombinedAuthInterceptor implements HttpInterceptor {
  constructor(private authService: AuthService, private router: Router) {}

  intercept(req: HttpRequest<any>, next: HttpHandler): Observable<HttpEvent<any>> {
    return this.authService.isLoggedIn$.pipe(
      first(),
      switchMap(isLoggedIn => {
        let modifiedReq = req;
        if (isLoggedIn) {
          const token = this.authService.getToken();
          if (token) {
            const headers = req.headers.append('Authorization', `Bearer ${token}`);
            modifiedReq = req.clone({ headers });
          }
        }
        return next.handle(modifiedReq).pipe(
          catchError((error: HttpErrorResponse) => {
            if (error.status === 401 || error.status === 403) {
              this.authService.logout();
              this.router.navigate(['/auth/login']);
            }
            return throwError(error);
          })
        );
      })
    );
  }
}

export const combinedAuthInterceptorProvider = {
  provide: HTTP_INTERCEPTORS,
  useClass: CombinedAuthInterceptor,
  multi: true,
};
