import { Component, OnInit } from '@angular/core';
import { AuthService } from 'src/app/core/auth/auth.service';
import { UIBootstrapService } from 'src/app/core/services/uibootstrap.service';

@Component({
  selector: 'app-sidebar',
  templateUrl: './sidebar.component.html',
  styleUrls: ['./sidebar.component.css']
})
export class SidebarComponent implements OnInit {
  public user: any = { data: {} };

  constructor(
    private _authService: AuthService, 
    private _uiInitService: UIBootstrapService
    ) { }

  ngOnInit(): void {
    this._uiInitService.closeSideNavigationOnWidthChange();
    this._uiInitService.toggleSideNavigation();
    this._uiInitService.activateFeatherIcons();

    this.fetchUserData();
  }

  fetchUserData() {
    const token = localStorage.getItem('token');
    this._adminService.get_admin(token).subscribe(
      response => {
        this.user = response;
        console.log("ðŸš€ ~ file: sidebar.component.ts:23 ~ SidebarComponent ~ fetchUserData ~ this.user:", this.user)
      },
      error => {
        console.error(error);

      }
    );
  }
}

