import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { InitialConfigService } from 'src/app/core/services/initial-config.service';

@Component({
  selector: 'app-activation',
  templateUrl: './activation.component.html',
  styleUrls: ['./activation.component.css']
})

export class ActivationComponent implements OnInit {
  activationForm!: FormGroup; // Usa "!" para indicar que será inicializada en ngOnInit
  token!: string; // Usa "!" para indicar que será inicializada en ngOnInit

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private formBuilder: FormBuilder,
    private _initialConfigService: InitialConfigService
  ) { }

  ngOnInit(): void {
    // Inicializa el formulario con validadores
    this.activationForm = this.formBuilder.group({
      inputPassword: ['', [Validators.required, Validators.minLength(8)]],
      inputConfirmPassword: ['', [Validators.required]]
    });

    // Obtiene el token de la URL
    this.route.params.subscribe(params => {
      this.token = params['token'];
      // Utiliza el token como sea necesario en tu lógica de activación.
    });
  }

  onActivate() {
    if (this.activationForm.valid) {
      // Llama a tu servicio para activar la cuenta
      this._initialConfigService.activateAccount(this.token, this.activationForm.value.password).subscribe(
        response => {
          // Maneja la respuesta exitosa
          // Redirige a una página de éxito o a donde sea necesario
          this.router.navigate(['/activation-success']);
        },
        error => {
          // Maneja los errores, por ejemplo, muestra un mensaje de error en la página
          console.error('Error al activar la cuenta:', error);
        }
      );
    }
  }
}
