import { Component } from '@angular/core';
import { GLOBAL } from 'src/app/core/config/GLOBAL';
import { FormBuilder, FormGroup, Validators, FormControl } from "@angular/forms";
import { Router } from '@angular/router';
import { AuthService } from 'src/app/core/auth/auth.service';
import { ToastService } from 'src/app/shared/services/toast.service';
import { ProfileData } from 'src/app/core/model/profile-data';
import { GeoInfoService } from 'src/app/shared/services/geo-info.service';


@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.css']
})
export class ProfileComponent {
  updateForm!: FormGroup;
  public user: any = { data: {} };
  submitted = false;


  country: any[] = [];
  countries: any[] = [];
  countries_arr: any[] = [];
  states: any[] = [];
  country_divGeo: string = '';
  country_phone_code: string = '';

  constructor(
    private _formBuilder: FormBuilder,
    private _authService: AuthService,
    private _router: Router,
    private _toastService: ToastService,
    private _geoInfoService : GeoInfoService
  ) {
    this.updateForm = this._formBuilder.group({
      inputUserName: ['', [Validators.required, Validators.minLength(12), Validators.maxLength(25),  Validators.pattern(/^\S*$/), Validators.pattern(/^[a-zA-Z0-9]*$/)]],
      inputFirstName: ['', [Validators.required, Validators.minLength(3), Validators.maxLength(25), Validators.pattern("^[a-zA-Z0-9\\s√±√ë]+$")]],
      inputLastName: ['', [Validators.required, Validators.minLength(3), Validators.maxLength(25), Validators.pattern("^[a-zA-Z0-9\\s√±√ë]+$")]],
      inputOrganizationName: ['', [Validators.required, Validators.minLength(3), Validators.maxLength(30), Validators.pattern("^[a-zA-Z0-9\\s√±√ë]+$")]],
      inputEmailAddress: ['', [Validators.required, Validators.email]],
      inputLocation: ['', [Validators.required]],
      inputPhoneNumber: ['', [Validators.required, Validators.pattern("[0-9]+")]],
      inputBirthday: ['', [Validators.required, this.validateDate.bind(this)]],
      inputRole: [{ value: '', disabled: true }, [Validators.required]],
      inputIdentification: [{ value: '', disabled: true }, [Validators.required]],
      inputAdditionalInfo: ['', [Validators.required, Validators.minLength(10), Validators.maxLength(50), Validators.pattern("^[a-zA-Z0-9\\s√±√ë]+$")]],
      inputProfileImage: ['', [Validators.required]],
    });
  }

  get f() {
    return this.updateForm.controls;
  }


  ngOnInit(): void {
    this.fetchUserData();
}



  fetchUserData() {
    this._authService.get_admin().subscribe(
      response => {
        this.user = response;
        console.log("üöÄ ~ file: profile.component.ts:48 ~ ProfileComponent ~ fetchUserData ~ this.user:", this.user)
        if (response.data === undefined) {
          this._router.navigate(['']);
        } else {
          const { userName, firstName, lastName, organizationName, emailAddress, location, phoneNumber, birthday, role, identification, additionalInfo, profileImage, createdAt, updatedAt } = response.data;
          const birthdayFormatted = this.convertDateFormat(response.data.birthday);          
          this.updateForm.patchValue({
            inputUserName: userName,
            inputFirstName: firstName,
            inputLastName: lastName,
            inputOrganizationName: organizationName,
            inputEmailAddress: emailAddress,
            inputLocation: location,
            inputPhoneNumber: phoneNumber,
            inputBirthday: birthdayFormatted,
            inputRole: role,
            inputIdentification: identification,
            inputAdditionalInfo: additionalInfo,
            inputProfileImage: profileImage,
            inputCreatedAt: createdAt,
            inputUpdatedAt: updatedAt,
          });

        }

      },
      error => {
        console.error(error);
      }
    );
  }



  onCountryChange() {

    this.states = [];
    this.countries_arr = [];


    const { country } = this.proveedor;
    const selectedCountryId = country;

    this._geoInfoService.get_States().subscribe(
      (response) => {
        response.forEach((element: any) => {

          if (element.country_id == selectedCountryId) {
            this.states.push({
              country_id: element.country_id,
              province_abbrev: element.province_abbrev,
              province_name: element.province_name
            });
          }
        });
        this.states.sort((a, b) => a.province_name.localeCompare(b.province_name));
      }
    );

    this._proveedorService.get_Countries().subscribe(
      (response) => {
        response.forEach((element: any) => {

          if (selectedCountryId == element.id) {
            this.countries_arr.push({
              country_id: element.id,
              country_abbrev: element.iso2,
              country_phone_code: element.prefix,
              country_divGeo: element.divGeo,
            });
          }
        });
        const firstElement = this.countries_arr[0];
        this.country_divGeo = firstElement ? firstElement.country_divGeo : '';
        this.country_phone_code = firstElement ? firstElement.country_phone_code : '';
        this.proveedor.phone = this.country_phone_code + (this.proveedor.phone || '').replace(this.country_phone_code, '');

      }
    );
  }


private convertDateFormat(dateString: string): string {
    const date = new Date(dateString);
    const year = date.getFullYear();
    const month = String(date.getMonth() + 1).padStart(2, '0');
    const day = String(date.getDate()).padStart(2, '0');
    return `${year}-${month}-${day}`;
}

private validateDate(control: FormControl): { [key: string]: any } | null {
  const inputDateStr: string = control.value;
  if (!/^\d{4}-\d{2}-\d{2}$/.test(inputDateStr)) {
      return { 'invalidDateFormat': true };
  }
  return null;
}



  update() {

    this.submitted = true;
  }


}
