import { Injectable } from '@angular/core';
import { BehaviorSubject, ignoreElements, map, Observable, tap } from 'rxjs';
import { GLOBAL } from '../config/GLOBAL';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { JwtHelperService } from '@auth0/angular-jwt';
import { Router } from '@angular/router';

import { LoginCredentials } from '../model';
import { UserWithToken } from '../model/user.interface';

const USER_LOCAL_STORAGE_KEY = 'token';



@Injectable({
  providedIn: 'root',
})
export class AuthService {

  public url;

  constructor(private _http: HttpClient, private _router: Router) {
    this.url = GLOBAL.url;
  }


  public isAuthenticated(allowRoles: string[]): boolean {
    const token = localStorage.getItem('token');

    if (!token) {
      return false;
    }

    try {
      const helper = new JwtHelperService();
      const decodedToken = helper.decodeToken(token);

      if (!decodedToken || !decodedToken['role']) {
        localStorage.removeItem('token');
        return false;
      }
      return allowRoles.includes(decodedToken['role']);
    } catch (error) {
      console.error('Error decoding token:', error);
      localStorage.removeItem('token');
      return false;
    }
  }


  getToken() {
    return localStorage.getItem('token');
  }



  check_admin_exists(): Observable<any> {
    let headers = new HttpHeaders().set('Content-Type', 'application/json');
    return this._http.get<any>(this.url + 'check_admin_exists', { headers: headers });
  }

  createAdmin(data: any): Observable<any> {
    let headers = new HttpHeaders().set('Content-Type', 'application/json');
    return this._http.post(this.url + 'create_admin', data, { headers: headers });
}

login_admin(credentials: LoginCredentials): Observable<any> {
  let headers = new HttpHeaders().set('Content-Type', 'application/json');
  return this._http.post<UserWithToken>(this.url + 'login_admin', credentials, { headers: headers }).pipe(
    tap((response: LoginCredentials) => {
      if (response && response.UserWithToken) {
        this.storeToken(response.token);
        this._router.navigate(['/']);
      }
    })
  );
}

storeToken(token: string) {
  localStorage.setItem('token', token);
}

  get_admin(token: any): Observable<any> {
    let headers = new HttpHeaders({ 'Content-Type': 'application/json', 'Authorization': token });
    return this._http.get(this.url + 'get_admin', { headers: headers });
  }

  logout(): void {
    this.removeUserFromLocalStorage();
    this._router.navigateByUrl('/login');
  }

  private redirectToDashboard(): void {
    this._router.navigateByUrl('/dashboard');
  }

  // private pushNewUser(token: string) {
  //   this.user.next(this.decodeToken(token));
  // }

  // private decodeToken(userToken: string): UserWithToken {
  //   const userInfo = JSON.parse(window.atob(userToken)) as User;

  //   return { ...userInfo, token: userToken };
  // }

  // private loadUserFromLocalStorage(): void {
  //   const userFromLocal = localStorage.getItem(USER_LOCAL_STORAGE_KEY);
  //   userFromLocal && this.pushNewUser(userFromLocal);
  // }

  private saveTokenToLocalStore(userToken: string): void {
    localStorage.setItem(USER_LOCAL_STORAGE_KEY, userToken);
  }

  private removeUserFromLocalStorage(): void {
    localStorage.removeItem(USER_LOCAL_STORAGE_KEY);
  }
}
