import { Component } from '@angular/core';
import { FormGroup, FormControl, Validators } from '@angular/forms';
import { AuthService } from 'src/app/core/services/auth.service';
import { ToastService } from 'src/app/shared/services/toast.service';

@Component({
  selector: 'app-forgot-password',
  templateUrl: './forgot-password.component.html',
  styleUrls: ['./forgot-password.component.css']
})
export class ForgotPasswordComponent {
  forgotPasswordForm = new FormGroup({
    emailAddress: new FormControl('', [Validators.required, Validators.email]),
  });

  // Variable para manejar mensajes de éxito/error
  message: string = '';

  constructor(
    private _authService: AuthService,
    private _toastService: ToastService
  ) { }

  onSubmit(): void {
    if (this.forgotPasswordForm.valid) {
      // Extrae emailAddress usando desestructuración y el operador de afirmación no nula
      const { emailAddress } = this.forgotPasswordForm.value;
      this._authService.forgotPassword({ emailAddress: emailAddress! }).subscribe({
        next: (response) => {
          // Maneja la respuesta exitosa aquí
          this._toastService.showToast('error', 'An error occurred while trying to send the password reset link.')
          this.message = 'If the email exists in our system, a password reset link will be sent.';
          // Opcional: resetear el formulario o redirigir al usuario
          this.forgotPasswordForm.reset();
        },
        error: (error) => {
          this._toastService.showToast('error', 'An error occurred while trying to send the password reset link.');
          // this.message = 'An error occurred while trying to send the password reset link.';
          console.error(error);
        }
      });
    }
  }

}
