import { Injectable } from '@angular/core';
import { AuthConfig, OAuthService } from 'angular-oauth2-oidc'
// import { JwtHelperService } from '@auth0/angular-jwt';

@Injectable({
  providedIn: 'root'
})
export class AuthGoogleService {
  constructor(private oauthService: OAuthService,
    private _authGoogleService: AuthGoogleService,
    private jwtHelper: JwtHelperService) {
    this.initLogin();
  }

  // public usuarioEstaVerificado(): boolean {
  //   const token = this._authGoogleService.getIdToken(); // Asegúrate de tener un método que exponga el ID token de manera segura
  //   if (!token) return false;

  //   const decodedToken = this.jwtHelper.decodeToken(token);
  //   return !!decodedToken.email_verified;
  // }



  public usuarioEstaAutenticado(): boolean {
    return this.oauthService.hasValidAccessToken();
  }

  initLogin() {
    const config: AuthConfig = {
      issuer: 'https://accounts.google.com',
      strictDiscoveryDocumentValidation: false,
      clientId: '386095253324-84qs8hfs2j1lccd29hlfr9393s27h5fc.apps.googleusercontent.com',
      redirectUri: window.location.origin + '/dashboard',
      scope: 'openid profile email',
    }

    this.oauthService.configure(config);
    this.oauthService.setupAutomaticSilentRefresh();
    this.oauthService.loadDiscoveryDocumentAndTryLogin();
  }

  login() {
    this.oauthService.initLoginFlow();
  }

  logout() {
    this.oauthService.logOut();
  }

  getProfile() {
    return this.oauthService.getIdentityClaims();
  }

  // Dentro de AuthGoogleService
  public getIdToken(): string | null {
    return this.oauthService.getIdToken();
  }


}
