import { Component } from '@angular/core';
import { FormGroup, FormControl, Validators } from '@angular/forms';
import { AuthService
@Component({
  selector: 'app-forgot-password',
  templateUrl: './forgot-password.component.html',
  styleUrls: ['./forgot-password.component.css']
})
export class ForgotPasswordComponent {
  forgotPasswordForm = new FormGroup({
    emailAddress: new FormControl('', [Validators.required, Validators.email]),
  });

  // Variable para manejar mensajes de éxito/error
  message: string = '';

  constructor(private authService: AuthService) { }

  onSubmit(): void {
    if (this.forgotPasswordForm.valid) {
      this.authService.forgotPassword(this.forgotPasswordForm.value).subscribe({
        next: (response) => {
          // Maneja la respuesta exitosa aquí
          this.message = 'Si el correo electrónico existe en nuestro sistema, se enviará un enlace de restablecimiento de contraseña.';
          // Opcional: resetear el formulario o redirigir al usuario
          this.forgotPasswordForm.reset();
        },
        error: (error) => {
          // Maneja errores aquí
          this.message = 'Ocurrió un error al intentar enviar el enlace de restablecimiento de contraseña.';
          console.error(error);
        }
      });
    }
  }
}
