import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { AuthService

@Component({
  selector: 'app-forgot-password',
  templateUrl: './forgot-password.component.html',
  styleUrls: ['./forgot-password.component.css']
})
export class ForgotPasswordComponent {
  forgotPasswordForm = new FormGroup({
    email: new FormControl('', [Validators.required, Validators.email]),
  });

  constructor(private _authService: AuthService) { }

  onSubmit() {
    if (this.forgotPasswordForm.valid) {
      this._authService.forgotPassword(this.forgotPasswordForm.value.email)
        .subscribe({
          next: (response) => {
            // Manejar respuesta exitosa
            alert('Si el correo electr칩nico existe en nuestro sistema, se enviar치 un enlace de restablecimiento de contrase침a.');
          },
          error: (error) => {
            // Manejar error
            console.error('Error al solicitar el restablecimiento de contrase침a:', error);
          }
        });
    }
  }
}
