import { Injectable } from '@angular/core';
declare const google: any; // Permite el uso de Google Identity Services en TypeScript

@Injectable({
  providedIn: 'root',
})
export class AuthGoogleService {

  constructor() { }

  initializeGoogleSignIn(clientId: string) {
    google.accounts.id.initialize({
      client_id: clientId,
      callback: (response: any) => this.handleCredentialResponse(response),
    });
    google.accounts.id.prompt();
  }

  handleCredentialResponse(response: any) {
    const token = response.credential;
    const userPayload = this.decodeJWTToken(token);
    sessionStorage.setItem('loggedInUser', JSON.stringify(userPayload));
    window.location.href = '/dashboard';
  }

  decodeJWTToken(token: string) {
    return JSON.parse(atob(token.split('.')[1]));
  }

  handleSignout() {
    google.accounts.id.disableAutoSelect();
    sessionStorage.removeItem('loggedInUser');
    // Usa el Router de Angular para la redirección para aprovechar el SPA-routing
    window.location.href = '/login';
  }
}

