import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { InitialConfigService } from 'src/app/core/services/initial-config.service';

@Component({
  selector: 'app-verification-pending',
  templateUrl: './verification-pending.component.html',
  styleUrls: ['./verification-pending.component.css']
})

export class VerificationPendingComponent implements OnInit {
  email: string = ''; // Inicializar con una cadena vacía

  constructor(private _route: ActivatedRoute, private _initialConfigService: InitialConfigService) { }

  ngOnInit() {
    const emailParam = this._route.snapshot.paramMap.get('email');
    if (emailParam !== null) {
      this.email = emailParam; // Asegurarse de que emailParam no sea null
    } else {
      // Manejar el caso donde emailParam es null
      // Por ejemplo, redirigir al usuario o mostrar un mensaje de error
    }
  }

  resendVerificationEmail() {
    this._initialConfigService.resendVerificationEmail(this.email).subscribe(
      response => {
        // Manejar la respuesta
      },
      error => {
        // Manejar el error
      }
    );
  }
}
