import { Injectable } from '@angular/core';
import { AuthConfig, OAuthService } from 'angular-oauth2-oidc'

@Injectable({
  providedIn: 'root'
})
export class AuthGoogleService {

  constructor(private oauthService: OAuthService) {

  }

  public authenticateUser() {
    const item = localStorage.getItem('googleAuth');
    const googleAuth = item ? JSON.parse(item) : null;

    if (googleAuth) {
      const payload = this.decodeJWTToken(googleAuth.credential);
      // Aquí puedes usar la información de payload, como el email del usuario, para autenticar en tu backend
    }
  }

  private decodeJWTToken(token: string): any {
    const base64Url = token.split('.')[1];
    const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
    const jsonPayload = decodeURIComponent(atob(base64).split('').map(function (c) {
      return '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2);
    }).join(''));

    return JSON.parse(jsonPayload);
  }

}

