import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { AuthService } from 'src/app/core/services/auth.service';
import { ApiResponse } from '../path/to/your/models/api-response.interface';


@Component({
  selector: 'app-forgot-password',
  templateUrl: './forgot-password.component.html',
  styleUrls: ['./forgot-password.component.css']
})
export class ForgotPasswordComponent {
  forgotPasswordForm = new FormGroup({
    email: new FormControl('', [Validators.required, Validators.email]),
  });

  constructor(private _authService: AuthService) { }

  onSubmit() {
    if (this.forgotPasswordForm.valid) {
      this._authService.forgotPassword(this.forgotPasswordForm.value.email)
        .subscribe({
          next: (response: ApiResponse<any>) => {
            // Manejar respuesta exitosa
            alert('Si el correo electrónico existe en nuestro sistema, se enviará un enlace de restablecimiento de contraseña.');
          },
          error: (error: any) => {
            // Manejar error
            console.error('Error al solicitar el restablecimiento de contraseña:', error);
          }
        });
    }
  }
}
