import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { InitialConfigService } from 'src/app/core/services/initial-config.service';
import { ToastService } from 'src/app/shared/services/toast.service';

@Component({
  selector: 'app-resend-activation-email',
  templateUrl: './resend-activation-email.component.html',
  styleUrls: ['./resend-activation-email.component.css']
})
export class ResendActivationEmailComponent implements OnInit {
  resendEmailForm: FormGroup;

  constructor(
    private formBuilder: FormBuilder,
    private initialConfigService: InitialConfigService,
    private toastService: ToastService
  ) {
    this.resendEmailForm = this.formBuilder.group({
      email: ['', [Validators.required, Validators.email]]
    });
  }

  ngOnInit(): void {
  }

  resendVerificationEmail() {
    if (this.resendEmailForm.valid) {
      const email = this.resendEmailForm.get('email')?.value;
      this.initialConfigService.resendVerificationEmail(email).subscribe({
        next: (response) => {
          this.toastService.showToast('success', `Correo de verificación enviado a ${email}.`);
        },
        error: (error) => {
          this.toastService.showToast('error', 'Ocurrió un error al reenviar el correo de verificación.');
        }
      });
    } else {
      this.toastService.showToast('error', 'Por favor, ingresa un correo electrónico válido.');
    }
  }
}
