import { Injectable } from '@angular/core';
import { AuthConfig, OAuthService, OAuthErrorEvent } from 'angular-oauth2-oidc';

@Injectable({
  providedIn: 'root',
})
export class AuthGoogleService {
  private config: AuthConfig = {
    issuer: 'https://accounts.google.com',
    redirectUri: window.location.origin + '/dashboard',
    clientId:
      '386095253324-hu7fnt80k4mqc18p3ob357f215pa2as5.apps.googleusercontent.com',
    scope: 'openid profile email',
    strictDiscoveryDocumentValidation: false,
    // issuer: 'https://accounts.google.com',
    // strictDiscoveryDocumentValidation: false,
    // clientId: '386095253324-hu7fnt80k4mqc18p3ob357f215pa2as5.apps.googleusercontent.com',
    // redirectUri: window.location.origin + '/dashboard',
    // scope: 'openid profile email',
  };

  constructor(private oauthService: OAuthService) {
    this.initLogin();
  }

  initLogin() {
    this.oauthService.configure(this.config);
    this.oauthService.setupAutomaticSilentRefresh();
    this.oauthService.loadDiscoveryDocumentAndTryLogin();
  }


  login() {
    this.oauthService.initLoginFlow();
  }

  logout() {
    this.oauthService.logOut();
  }

  getProfile() {
    return this.oauthService.getIdentityClaims();
  }

  isAuthenticated(): boolean {
    return this.oauthService.hasValidAccessToken();
  }

}
