import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router'; // Importar Router

declare var google: any;

interface GoogleSignInResponse {
  clientId: string;
  credential: string;
  select_by: string;
}

@Component({
  selector: 'app-google-signin',
  templateUrl: './google-signin.component.html',
  styleUrls: ['./google-signin.component.css']
})

export class GoogleSigninComponent implements OnInit {
  constructor(private router: Router) { } // Inyectar Router

  ngOnInit(): void {
    this.renderButton();
  }

  renderButton() {
    google.accounts.id.initialize({
      client_id: "142003720401-scrooc5tq3p8ht8m377p67kdrt3ablei.apps.googleusercontent.com",
      callback: (response: GoogleSignInResponse) => this.onSignIn(response),
    });

    google.accounts.id.renderButton(
      document.getElementById("g_id_onload"),
      { theme: "outline", size: "large" }
    );

    google.accounts.id.prompt();
  }

  onSignIn(response: GoogleSignInResponse) {
    console.log('Usuario ha iniciado sesión:', response);
    const userToken = response.credential; // El token JWT
    sessionStorage.setItem('userToken', userToken);
    // Decodifica el JWT para obtener datos del usuario (opcional, dependiendo de tus necesidades)
    this.router.navigate(['/dashboard']);
  }

}
