import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { InitialConfigService } from 'src/app/core/services/initial-config.service';
import { SharedDataBetweenStepService } from 'src/app/core/services/shared-data-between-step.service';
import { ToastService } from 'src/app/shared/services/toast.service';



@Component({
  selector: 'app-verification-pending',
  templateUrl: './verification-pending.component.html',
  styleUrls: ['./verification-pending.component.css']
})

export class VerificationPendingComponent implements OnInit {
  email: string = ''; // Inicializar con una cadena vacía

  constructor(
    private _route: ActivatedRoute,
    private _initialConfigService: InitialConfigService,
    private _sharedDataBetweenStepService: SharedDataBetweenStepService,
    private _toastService: ToastService,

  ) { }

  ngOnInit() {
    this.email = this._sharedDataBetweenStepService.getEmailAddress();
    if (!this.email) {
      this._toastService.showToast('error', 'emailAddress no existe');
      // por ejemplo, redirigiendo al usuario o mostrando un mensaje de error.
    }
  }

  resendVerificationEmail() {
    if (this.email) {
      this._initialConfigService.resendVerificationEmail(this.email).subscribe(
        response => {
          this._toastService.showToast('success', 'An email has been sent to the ' + ' ' + this.email);
        },
        error => {
          this._toastService.showToast('error', 'ha ocurrido un error.');
        }
      );
    }
  }
}
