import { Component, OnInit } from '@angular/core';

declare var google: any;

@Component({
  selector: 'app-google-signin',
  templateUrl: ['./google-signin.component.html'],
  styleUrls: ['./google-signin.component.css']
})
export class GoogleSigninComponent implements OnInit {

  constructor() { }

  ngOnInit(): void {
    this.renderButton();
  }

  renderButton() {
    google.accounts.id.initialize({
      client_id: "142003720401-scrooc5tq3p8ht8m377p67kdrt3ablei.apps.googleusercontent.com",
      callback: this.onSignIn,
    });

    google.accounts.id.renderButton(
      document.getElementById("g_id_onload"), // El elemento donde se renderizará el botón
      { theme: "outline", size: "large" }  // Opciones de personalización
    );

    google.accounts.id.prompt(); // Mostrar el prompt de sesión si es necesario
  }

  onSignIn(response: any) {
    // Lógica para manejar la respuesta de inicio de sesión
    console.log('Usuario ha iniciado sesión:', response);
  }
}
