import { Router } from "@angular/router";
declare var handleSignout: any; // Declare the global function to avoid TypeScript errors
@Component({
  selector: 'app-admin-dashboard',
  templateUrl: './admin-dashboard.component.html',
  styleUrls: ['./admin-dashboard.component.css']
})
export class AdminDashboardComponent implements OnInit {
  constructor(private router: Router) { }
  userProfile: any;

  ngOnInit() {
    // // Suponiendo que esta es la respuesta de inicio de sesión que has mostrado
    // const userResponse = {
    //   clientId: '386095253324-8cm1u3k9llp1nacpdt75vrlnc8on0ofa.apps.googleusercontent.com',
    //   credential: 'eyJhbGciOiJSUzI1NiIsImtpZCI6ImVkODA2ZjE4NDJiNTg4MD…m8_TitIhK07k4JrPSlR0eGsOn-tkr0KnZUNlXAkIFECRtO6_g',
    //   select_by: 'user_1tap'
    // };

    // // Aquí es donde decides qué parte de esta respuesta necesitas almacenar
    // // Por ejemplo, podrías querer almacenar el objeto completo o solo ciertas propiedades
    // sessionStorage.setItem("loggedInUser", JSON.stringify(userResponse));

    // // Ahora, userProfile se establece con la información almacenada o con un valor predeterminado
    // this.userProfile = JSON.parse(sessionStorage.getItem("loggedInUser") || "{}");
  }

  handleSignOut() {
    // Tu lógica existente para manejar el cierre de sesión
    handleSignout();
    sessionStorage.removeItem("loggedInUser");
    this.router.navigate(["/auth/login"]).then(() => {
      window.location.reload();
    });
  }
}


