import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router'; // Importar Router
// import { jwtDecode } from 'jwt-decode';
import { AuthService } from 'src/app/core/services/auth.service';}

declare var google: any;

interface GoogleSignInResponse {
  clientId: string;
  credential: string;
  select_by: string;
}

@Component({
  selector: 'app-google-signin',
  templateUrl: './google-signin.component.html',
  styleUrls: ['./google-signin.component.css']
})

export class GoogleSigninComponent implements OnInit {
  constructor(private router: Router
  ) { } // Inyectar Router

  ngOnInit(): void {
    this.renderButton();
  }

  renderButton() {
    google.accounts.id.initialize({
      client_id: "142003720401-scrooc5tq3p8ht8m377p67kdrt3ablei.apps.googleusercontent.com",
      callback: (response: GoogleSignInResponse) => this.onSignIn(response),
    });

    google.accounts.id.renderButton(
      document.getElementById("g_id_onload"),
      { theme: "outline", size: "large" }
    );

    google.accounts.id.prompt();
  }

  // //  En tu método onSignIn
  // onSignIn(response: GoogleSignInResponse) {
  //   console.log('Usuario ha iniciado sesión:', response);
  //   const userToken = response.credential;
  //   sessionStorage.setItem('userToken', userToken);

  //   // Decodifica el token JWT para obtener datos del usuario
  //   const decodedToken = jwtDecode(userToken);
  //   console.log(decodedToken);

  //   // Navega al dashboard
  //   this.router.navigate(['/dashboard']);
  // }

  // En tu componente de inicio de sesión de Google

  onSignIn(googleUser) {
    const id_token = googleUser.getAuthResponse().id_token;
    this._authService.loginWithGoogle(id_token).subscribe({
      next: (response) => {
        // Manejar la respuesta, por ejemplo, almacenar el token de sesión, redirigir al usuario, etc.
      },
      error: (error) => {
        console.error('Error al iniciar sesión con Google:', error);
      }
    });
  }


}
