import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { ToastService } from 'src/app/shared/services/toast.service';
import { BusinessConfigService } from 'src/app/core/services/business-config.service';

@Component({
  selector: 'app-business-setup',
  templateUrl: './business-setup.component.html',
  styleUrls: ['./business-setup.component.css']
})
export class BusinessSetupComponent implements OnInit {
  businessSetupForm!: FormGroup;
  loading: boolean = false;

  constructor(
    private formBuilder: FormBuilder,
    private router: Router,
    private toastService: ToastService,
    private businessService: BusinessConfigService
  ) { }

  ngOnInit(): void {
    this.businessSetupForm = this.formBuilder.group({
      businessName: ['', [Validators.required, Validators.maxLength(100)]],
      taxId: ['', [Validators.required, Validators.pattern('[A-Z0-9]+')]],
      paymentGateway: ['', Validators.required],
      physicalLocation: ['', Validators.required],
      shippingService: ['', Validators.required],
      companyEmail: ['', [Validators.required, Validators.email]],
      customerServiceEmail: ['', [Validators.required, Validators.email]],
      returnsEmail: ['', [Validators.required, Validators.email]],
      currency: ['', Validators.required],
      // Consider adding file upload validators if necessary
      logo: [null]
    });
  }

  onFileChange(event: any) {
    if (event.target.files.length > 0) {
      const file = event.target.files[0];
      this.businessSetupForm.patchValue({
        logo: file
      });
    }
  }

  submitBusinessDetails() {
    if (this.businessSetupForm.invalid) {
      this.toastService.showToast('error', 'Please fill all required fields correctly.');
      return;
    }

    this.loading = true;
    // Aquí iría la lógica para enviar los datos al servidor, incluyendo la carga del archivo del logo
    // Simulamos una llamada al servicio de negocio
    this.businessService.submitBusinessDetails(this.businessSetupForm.value).subscribe({
      next: (response) => {
        this.toastService.showToast('success', 'Business details saved successfully.');
        this.router.navigate(['/next-step-route']);
      },
      error: (error) => {
        this.toastService.showToast('error', 'An error occurred while saving business details.');
        console.error('Error saving business details:', error);
      },
      complete: () => this.loading = false
    });
  }
}
